SELECT c.kh_id khId, c.member_id memberId, m.name, c.khmc, c.khsfzh, c.khlx, c.khcjlx, c.khcjsj, c.khxb, c.khsjh, c.khqq, c.khqqnc, c.khwx, c.khwxnc, c.khzj, c.khdz, c.cjsj, c.xgsj, c.cjr_id cjrId, c.bz, c.byzd, c.byzd1, c.byzd2, c.byzd3 FROM client c LEFT JOIN member m ON c.member_id=m.member_id LEFT JOIN member_department md on md.member_id=m.member_id WHERE 1=1
AND c.member_id = #{memberId}
AND md.department_id= #{departmentId}
AND c.khmc= #{khmc}
AND c.khlx= #{khlx}
AND c.khcjlx= #{khcjlx}
AND c.khsjh= #{khsjh}
AND c.khqq= #{khqq}
AND c.department_id= #{departmentId}
AND c.cjsj >= #{cjsjQ}
AND #{cjsjZ} >c.cjsjZ
AND c.byzd = #{byzd}
order by c.cjsj DESC

SELECT * FROM
(SELECT COUNT(1) khCount
    FROM client
    WHERE member_id=#{memberId}
      AND cjsj>=#{start}
      AND #{end}>=cjsj
    ) a ,
(SELECT COUNT(1) zzCount
    FROM client_log cl LEFT JOIN client c ON c.kh_id =cl.kh_id
    WHERE c.member_id=#{memberId}
    AND cl.cjsj >= #{start}
      AND #{end} >= cl.cjsj
    ) b

