SELECT COUNT(*) AS zzCount,DATE_FORMAT(cjsj,'%Y-%m-%d') AS tjrq FROM client_log cl left join client_member cm on cl.kh_id=cm.kh_id left join member_department md on md.member_id = cm.member_id WHERE cjsj>=#{start} AND #{end}> cjsj
AND cm.member_id = #{memberId}
AND md.department_id = #{departmentId}
GROUP BY tjrq

SELECT COUNT(*) AS khCount,DATE_FORMAT(cjsj,'%Y-%m-%d') AS tjrq FROM client c left join member_department md on md.member_id = c.member_id WHERE cjsj>=#{start} AND #{end}> cjsj
AND cm.member_id = #{memberId}
AND md.department_id = #{departmentId}
GROUP BY tjrq
