mapper.select:
SELECT c.kh_id khId, c.member_id memberId, m.name, c.khmc, c.khsfzh, c.khlx, c.khcjlx, c.khxb, c.khsjh, c.khqq, c.khqqnc, c.khwx, c.khwxnc, c.khzj, c.khdz, c.cjsj, c.xgsj, c.cjr_id cjrId, c.bz, c.byzd, c.byzd1, c.byzd2, c.byzd3 FROM CLIENT c LEFT JOIN member m ON c.member_id=m.member_id LEFT JOIN member_department md on md.member_id=m.member_id WHERE 1=1
AND c.member_id = #{memberId}
AND md.department_id= #{departmentId}
