mapper.insert:
insert into user (id, user_name, password, 
  status, creat_time, creat_user_id
  )
values (#{id,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{creatTime,jdbcType=TIMESTAMP}, #{creatUserId,jdbcType=INTEGER}
  )

mapper.select:
SELECT

FROM USER
WHERE user_name=#{userName}
AND PASSWORD=#{password}
AND STATUS=1

SELECT mr.role_id
FROM user_member um LEFT JOIN member_role mr ON um.member_id = mr.member_id
WHERE um.user_id = #{id};

SELECT user_id
FROM user_member
WHERE member_id=#{memberId};

mapper.update:
update user
set password = #{newPassword,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
